// Function to switch language
function switchLanguage(language) {
  document.querySelector(".nav a[href='#']").textContent = translations[language].home;
  document.querySelector(".nav a[href='#about']").textContent = translations[language].about;
  document.querySelector(".nav a[href='#services']").textContent = translations[language].services;
  document.querySelector(".nav a[href='#contact']").textContent = translations[language].contact;
  document.querySelector(".banner-area h1").innerHTML = translations[language].bannerTitle;
  document.querySelector(".banner-area h3").textContent = translations[language].bannerSubtitle;
  document.querySelector("#about h3").textContent = translations[language].aboutTitle;
  document.querySelector(".about-right h2").textContent = translations[language].aboutText;
  document.querySelector(".msg-area h2").textContent = translations[language].quoteTitle;
  document.querySelector(".msg-area p").textContent = translations[language].quoteText;
  document.querySelector(".about-right p").textContent = translations[language].aboutParagraph;
  document.querySelector("#contact h3").textContent = translations[language].contactTitle;
  document.querySelector("footer p").textContent = translations[language].footerText;
  document.querySelector(".services-area h3").textContent = translations[language].serviceTitle;

  // Update service section
  const services = [
    {
      title: translations[language].serviceProgram,
      text: translations[language].programText,
    },
    {
      title: translations[language].serviceTheology,
      text: translations[language].theologyText,
    },
    {
      title: translations[language].serviceMissonary,
      text: translations[language].missonaryText,
    },
  ];

  const serviceItems = document.querySelectorAll(".services-content li");
  services.forEach((service, index) => {
    if (serviceItems[index]) {
      serviceItems[index].querySelector("h4").textContent = service.title;
      serviceItems[index].querySelector("p").textContent = service.text;
    }
  });
}

// Event listener for language switch
document.getElementById("languageSwitcher").addEventListener("change", (event) => {
  switchLanguage(event.target.value);
});